#!/bin/bash
tmux new-session \; send-keys "/bin/bash -c 'resources/alacarte.sh $1 nmap | tee results/$1/alacarte/nmap.txt'" C-m \; split-window -h \; send-keys "/bin/bash -c 'resources/alacarte.sh $1 dir_http | tee results/$1/alacarte/dir_http.txt'" C-m \; split-window -v -p 66 \; send-keys "/bin/bash -c 'resources/alacarte.sh $1 dir_https | tee results/$1/alacarte/dir_https.txt'" C-m \; split-window -v \; send-keys "/bin/bash -c 'resources/alacarte.sh $1 smb | tee results/$1/alacarte/smb.txt'" C-m \; select-pane -t 0 \; split-window -v -p 66\; send-keys "/bin/bash -c 'resources/alacarte.sh $1 smtp | tee results/$1/alacarte/smtp.txt'" C-m \; split-window -v \; send-keys "/bin/bash -c 'resources/alacarte.sh $1 snmp | tee results/$1/alacarte/snmp.txt'" C-m \;
